<?php $bucket = 'BUCKET';// these can be found on your Account page, under Security Credentials > Access Keys$accessKeyId = 'ACCESS_KEY_ID';$secret = 'SECRET_ACCESS_KEY';// prepare policy$policy = base64_encode(json_encode(array(	// ISO 8601 - date('c'); generates uncompatible date, so better do it manually	'expiration' => date('Y-m-d\TH:i:s.000\Z', strtotime('+1 day')),  	'conditions' => array(		array('bucket' => $bucket),		array('acl' => 'public-read'),		array('starts-with', '$key', ''),		// for demo purposes we are accepting only images		array('starts-with', '$Content-Type', 'image/'),		// Plupload internally adds name field, so we need to mention it here		array('starts-with', '$name', ''), 			// One more field to take into account: Filename - gets silently sent by FileReference.upload() in Flash		// http://docs.amazonwebservices.com/AmazonS3/latest/dev/HTTPPOSTFlash.html		array('starts-with', '$Filename', ''), 	))));// sign policy$signature = base64_encode(hash_hmac('sha1', $policy, $secret, true));?><div class="page-title">Création d'un lot</div><!--<br><br><div class="classic-form" style="width:650px;margin-left:5px;">-->    <div style="margin-top:10px;">		<?= Form::select('projet', $tb_proj, null, array('id' => 'pro_id' , 'class'=>'classic-input' , 'style' => 'width: 200px !important;margin-left: 10px;')); ?>					<!--	<span class="btn fileinput-button classic-input" style="marging-left:10px;">			<i class="glyphicon glyphicon-plus"></i>			<span>Sélectionner le ou les fichiers ZIP à importer (Max: 64Mo par fichier ZIP)</span>			<input id="fileupload" type="file" accept="application/zip" name="lot" onclick="before_upload()" multiple>		</span>-->    </div>    <!--<br>    <br>    <div id="progress" class="progress">        <div class="progress-bar progress-bar-success"></div>    </div>    <div id="files" class="files"></div>    <br>--><!--</div>--><div id="uploader" style="margin-top:10px;">	</div><div id="